import { useState } from 'react';
import axios from 'axios';

const API_BASE = process.env.REACT_APP_API_BASE || "http://127.0.0.1:5000";

const TASKS = [
  {
    id: 'infection',
    label: 'üß¨ ◊ó◊°◊ô◊û◊™ ◊î◊ì◊ë◊ß◊î',
    description:
      '◊î◊ï◊ï◊ô◊®◊ï◊° ◊û◊ñ◊®◊ô◊ß ◊ß◊ï◊ì ◊ú◊ß◊ë◊¶◊ô Python ◊ê◊ï Bash ◊ë◊™◊ô◊ß◊ô◊ô◊™ ◊î◊û◊ò◊®◊î. ◊û◊ò◊®◊™◊ö ◊ú◊ñ◊î◊ï◊™ ◊™◊î◊ú◊ô◊ö ◊©◊û◊†◊°◊î ◊ú◊î◊§◊¢◊ô◊ú trigger_ransom.py ◊û◊™◊ï◊ö ◊ß◊ï◊ë◊• ◊ê◊ó◊® ◊ï◊ú◊î◊í◊ô◊ë ◊ë◊î◊™◊ê◊ù.',
    tools: [
      '◊î◊ß◊ï◊ë◊• trigger_ransom.py ◊û◊û◊ï◊ß◊ù ◊ë÷æ/tmp/data',
      '◊î◊ê◊†◊ò◊ô ◊ï◊ô◊®◊ï◊° ◊©◊ú◊ö ◊®◊• ◊ë◊®◊ß◊¢ ◊õ◊ú ◊î◊ñ◊û◊ü',
      '◊ú◊ê◊ó◊® ◊ñ◊ô◊î◊ï◊ô ◊î◊©◊™◊û◊© ◊ë◊§◊ï◊†◊ß◊¶◊ô◊î kill_process ◊õ◊ì◊ô ◊ú◊¢◊¶◊ï◊® ◊ê◊™ ◊î◊™◊î◊ú◊ô◊ö'
    ]
  },
  {
    id: 'encrypt',
    label: 'üîê ◊û◊†◊ô◊¢◊™ ◊î◊¶◊§◊†◊î',
    description:
      '◊î◊õ◊ï◊§◊®◊î ◊û◊¶◊§◊ô◊†◊î ◊ê◊™ ◊™◊ó◊ô◊ú◊™ ◊î◊ß◊ï◊ë◊• (10KB) ◊¢◊ù ◊û◊§◊™◊ó AES. ◊û◊ò◊®◊™◊ö ◊ú◊û◊†◊ï◊¢ ◊ê◊™ ◊§◊¢◊ï◊ú◊™ ◊î◊î◊¶◊§◊†◊î ◊ú◊§◊†◊ô ◊©◊û◊™◊®◊ó◊© ◊©◊ô◊†◊ï◊ô ◊ë◊§◊ï◊¢◊ú ◊ú◊ß◊ï◊ë◊•.',
    tools: [
      '◊î◊¶◊§◊†◊î ◊û◊™◊ë◊¶◊¢◊™ ◊í◊ù ◊î◊ô◊ê ◊¢◊ú ◊ß◊ë◊¶◊ô◊ù ◊ë÷æ/tmp/data',
      '◊ê◊ù ◊ñ◊ô◊î◊ô◊™ ◊†◊ô◊°◊ô◊ï◊ü ◊î◊¶◊§◊†◊î ‚Äì ◊¢◊¶◊ï◊® ◊ê◊™ ◊î◊™◊î◊ú◊ô◊ö ◊ë◊¢◊ñ◊®◊™ kill_process'
    ]
  },
  {
    id: 'decrypt',
    label: 'üóùÔ∏è ◊§◊¢◊†◊ï◊ó ◊ß◊ë◊¶◊ô◊ù',
    description:
      '◊ô◊© ◊ß◊ë◊¶◊ô◊ù ◊©◊î◊ï◊¶◊§◊†◊ï ◊û◊®◊ê◊©. ◊î◊û◊©◊ô◊û◊î ◊©◊ú◊ö ◊î◊ô◊ê ◊ú◊ñ◊î◊ï◊™ ◊ê◊ï◊™◊ù ◊ú◊§◊ô ◊ó◊™◊ô◊û◊î ("BME1") ◊ï◊ú◊ë◊¶◊¢ ◊ê◊™ ◊™◊î◊ú◊ô◊ö ◊î◊§◊¢◊†◊ï◊ó ◊ë◊¢◊ñ◊®◊™ ◊û◊§◊™◊ó ◊ô◊ì◊ï◊¢ ◊û◊®◊ê◊©.',
    tools: [
      '◊î◊ß◊ë◊¶◊ô◊ù ◊î◊û◊ï◊¶◊§◊†◊ô◊ù ◊û◊™◊ó◊ô◊ú◊ô◊ù ◊ë◊û◊ó◊®◊ï◊ñ◊™ BME1',
      '◊î◊©◊™◊û◊© ◊ë◊û◊§◊™◊ó ◊©◊°◊ï◊§◊ß ◊õ◊ì◊ô ◊ú◊§◊¢◊†◊ó ◊ï◊ú◊î◊ó◊ñ◊ô◊® ◊ê◊™ ◊î◊ß◊ï◊ë◊• ◊î◊û◊ß◊ï◊®◊ô'
    ]
  }
];

export default function PracticeExercise() {
  const [studentCode, setStudentCode] = useState('');
  const [output, setOutput] = useState('');
  const [showToolbox, setShowToolbox] = useState(false);
  const [currentTask, setCurrentTask] = useState(TASKS[0]);

  const runSimulation = async () => {
    try {
      await axios.post(`${API_BASE}/save-antivirus`, { code: studentCode });
      const res = await axios.post(`${API_BASE}/simulate`, { task: currentTask.id });
      setOutput(res.data.stdout || res.data.stderr || '');
    } catch {
      setOutput('‚ùå ◊©◊í◊ô◊ê◊î ◊ë◊î◊®◊¶◊î');
    }
  };

  return (
    <div className="flex flex-col h-screen bg-gray-900 text-white">
      <header className="bg-gray-800 px-4 py-2 flex justify-between items-center">
        <h1 className="text-xl font-bold">◊¢◊ï◊®◊ö ◊ê◊†◊ò◊ô ◊ï◊ô◊®◊ï◊° üíª</h1>
        <button
          onClick={() => setShowToolbox(!showToolbox)}
          className="bg-blue-600 hover:bg-blue-700 px-3 py-1 rounded text-sm"
        >
          ◊ê◊®◊í◊ñ ◊õ◊ú◊ô◊ù üß∞
        </button>
      </header>

      <div className="flex flex-1 overflow-hidden">
        <aside className="bg-gray-800 w-60 p-2 overflow-y-auto">
          <h2 className="mb-3 font-semibold">◊û◊©◊ô◊û◊ï◊™ ◊™◊®◊í◊ï◊ú üìÇ</h2>
          {TASKS.map(task => (
            <button
              key={task.id}
              className={`w-full text-left px-3 py-2 rounded mb-2 ${currentTask.id === task.id ? 'bg-blue-500' : 'bg-gray-700 hover:bg-gray-600'}`}
              onClick={() => setCurrentTask(task)}
            >
              {task.label}
            </button>
          ))}

          <div className="bg-gray-900 text-xs mt-4 p-2 rounded">
            <p className="font-bold mb-1">üß† {currentTask.label}</p>
            <p>{currentTask.description}</p>
          </div>
        </aside>

        <main className="flex-1 flex flex-col">
          <textarea
            className="flex-1 bg-black text-green-400 font-mono p-4 outline-none resize-none w-full"
            placeholder="◊õ◊™◊ï◊ë ◊õ◊ê◊ü ◊ê◊™ ◊ß◊ï◊ì ◊î◊ê◊†◊ò◊ô ◊ï◊ô◊®◊ï◊° ◊©◊ú◊ö"
            value={studentCode}
            onChange={e => setStudentCode(e.target.value)}
            dir="ltr"
          />

          <button
            className="bg-blue-600 hover:bg-blue-700 py-2 mx-4 my-2 rounded shadow"
            onClick={runSimulation}
          >
            üöÄ ◊î◊§◊¢◊ú ◊°◊ô◊û◊ï◊ú◊¶◊ô◊î
          </button>

          {output && (
            <pre className="bg-black m-4 p-4 rounded overflow-auto text-green-300 max-h-48">
              {output}
            </pre>
          )}
        </main>

        {showToolbox && (
          <aside className="bg-gray-800 w-64 p-4 border-l border-gray-700 overflow-auto">
            <h3 className="font-semibold mb-2">◊õ◊ú◊ô◊ù ◊ñ◊û◊ô◊†◊ô◊ù üõ†Ô∏è</h3>
            <ul className="list-disc text-sm pr-4">
              {currentTask.tools.map((tool, i) => (
                <li key={i}>{tool}</li>
              ))}
            </ul>
          </aside>
        )}
      </div>
    </div>
  );
}
